(function(name,theme){
	theme=theme||"standard";

	var border="1px solid gray";//"1px solid #6278AA";
	var color="#4E4E4E";
	var fontFamily="Arial,sans-serif";
	var fontSize="1.0em";
	switch(name){
		case"addressInput":return{
			"font-size":fontSize
			,"border":border
			,"color":color
			,"font-family":fontFamily
			,"padding-left":"10px"
			,"padding-right":"1px"
			,"margin-left":"5px"
			,"margin-right":"5px"
		};
		case"toolbar":return{
			"background-color":"#DEDEDE"//#C9E7FF"
			,"border-bottom":border
		};
		case"cape":return{
			"background-color":"silver"//#888888"//#8D7A77"
			,"border-top":border
		};
		case"container":return{
			"background-color":"white"
		};
	}
})
