<script>
export default {
	data: function() {
		return {
			user: {
				id: '',
				name: '',
				image: ''
			},
			contacts: {
				users: []
			},
			object: {
				id: '',
				name: '',
				image: '',
				active: 0
			},
			messages: [],
			imageUser: '',
			expanded: false
		}
	},
	computed: {
		statusObject: function() {
			return this.object.active == 0 ? "offline" : "online";
		},
		contactStatus: function() {
			return "contact-status-online";
		}
	},
	methods: {
		showExpandedInformation() {
			if(this.expanded) {
				$(".present").removeClass('hidden');
				$(".no-present").addClass('hidden');
				$("#contacts").css('height', 'calc(100% - 195px)');
				this.expanded = false;
			} else {
				$(".present").addClass('hidden');
				$(".no-present").removeClass('hidden');
				$("#contacts").css('height', 'calc(100% - 345px)');
				this.expanded = true;
			}
		},
		addMessage(message) {
			var app = this

			var messageSent = {
				message: message.message,
				user: app.user
			}

			app.messages.push(messageSent);

			axios.post('/messages', message)
			.then(function(refs) {})
			.catch(function(refs) {
				console.log(refs)
			})

			$("#messages").scrollTop($("#messages")[0].scrollHeight);
		},
		changeObject(user) {
			this.object.id = user.id
			this.object.name = user.name
			this.object.image = user.image
			this.object.active = user.active
		}
	},
	created() {
		var app = this
		
		axios.get('/messages')
		.then(function(refs) {
			app.user = refs.data.user

			refs.data.contacts.forEach(function($contact) {
				app.contacts.users.push($contact)
			});

			var object = refs.data.contacts[0]
			app.object.id = object.id
			app.object.name = object.name
			app.object.image = object.image
			app.object.active = object.active

			app.messages = refs.data.messages

			app.imageUser = refs.data.user.image
		})
		.catch(function(refs) {
			console.log(refs)
		})

		Echo.join('Chatroom')
		.listen('MessagePosted', (e) => {
			//Handle event
			app.messages.push({
				message: e.message.message,
				user: e.user
			})

			$("#messages").scrollTop($("#messages")[0].scrollHeight);
		});

		Echo.join('App.User.Login')
		.listen('UserSigned', (e) => {
			if(this.object.id == e.userId) {
				this.object.active = 1
			}

			this.contacts.users.forEach(function(user) {
				if(user.id == e.userId) {
					user.active = 1
				}
			})
		});

		Echo.join('App.User.Logout')
		.listen('UserLogged', (e) => {
			if(this.object.id == e.userId) {
				this.object.active = 0
			}

			this.contacts.users.forEach(function(user) {
				if(user.id == e.userId) {
					user.active = 0
				}
			})
		});
	}
} 
</script>